const handler = (input) => {
  console.log('Function Input:', JSON.stringify(input));
  const priorities = input.priority_list || [];
  const partners = input.partners || [];

  // Filter serviceable partners
  const serviceableMap = {};
  partners.forEach(p => {
    if (p.is_serviceable) {
      serviceableMap[p.partner_code] = p;
    }
  });

  // Find first priority that is serviceable
  let selected = null;
  let reason = 'No suitable partner found';

  for (const item of priorities) {
    // item is now an object: { partner_code, priority, id }
    if (serviceableMap[item.partner_code]) {
      selected = item;
      reason = 'Selected based on user priority: ' + item.partner_code + ' (Priority ' + item.priority + ')';
      break;
    }
  }

  return {
    selected_partner: selected,
    reason: reason
  };
}
